import React ,{useEffect} from 'react'
import Notification from './Notification'
import useConnectionHook from '../Hooks/connectionHook'
const {useSupplier} = require('../context/Refresh')

const Connection = () => {
    const {triggerUpdate} = useSupplier()
    const{connections , getConnections} = useConnectionHook()
    console.log(connections.length)
    useEffect(() => {
        getConnections()
    }, [triggerUpdate])
    // console.log(connections?.connections[0].userId.name)
  return (
<>
    <div className='mb-2'>
        <h1 className='text-3xl w-full text-center'>Notification</h1>
        <Notification />
    </div>
    <div className='w-screen border-t-2 border-gray-500'>
        <h1 className='w-full text-center text-3xl mt-3'> All User</h1>

        {
            <div className="w-full max-w-sm bg-white border border-gray-200 rounded-lg shadow mt-4 ml-4">

            <div className="flex flex-col items-center p-4 ">
                <img className="w-24 h-24 mb-3 rounded-full shadow-lg" src="https://img.freepik.com/free-vector/smiling-redhaired-boy-illustration_1308-176664.jpg?semt=ais_hybrid" alt="Bonnie image"/>
                <h5 className="mb-1 text-xl font-medium ">Bonnie Green</h5>
                <div className="flex mt-4 md:mt-6">
                    <button className="inline-flex items-center px-4 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Connect</button>
                </div>
            </div>
        </div>
        }
    </div>


</>

  )
}

export default Connection
