import React, { useState } from 'react';

const ProfilePage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  return (
    <div className={isModalOpen ? "overflow-hidden" : ""}>
      {/* Header */}
      <header className="relative flex items-center justify-center h-screen bg-cover bg-center text-center" 
        style={{ backgroundImage: "url('https://images.pexels.com/photos/129105/pexels-photo-129105.jpeg?w=1260&h=750&auto=compress&cs=tinysrgb')" }}>
        <div className="text-center">
          <div className="w-32 h-32 overflow-hidden rounded-full mx-auto mb-4">
            <img
              className="object-cover w-full h-full"
              src="https://scontent-sin6-1.xx.fbcdn.net/v/t1.0-9/15977755_10207841229656791_4781027181067511410_n.jpg?oh=3d09a537577b5d916689bc1b8a8d6d28&oe=5965881F"
              alt="Avatar"
            />
          </div>
          <h1 className="text-white text-3xl font-bold">AgilBAKA</h1>
          <button
            className="mt-4 px-4 py-2 bg-white rounded-full text-sm font-semibold shadow-md hover:bg-gray-100"
            onClick={openModal}
          >
            Edit Profile
          </button>
        </div>
      </header>

      {/* Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
          {/* Overlay */}
          <div
            className="absolute inset-0"
            onClick={closeModal}
          ></div>

          {/* Modal Content */}
          <div className="relative bg-white rounded-lg p-6 shadow-lg z-10 max-w-md w-full">
            <h2 className="text-lg font-semibold mb-4">Hello, it's me!</h2>
            <p className="text-gray-700">This is the content of the modal.</p>
            <button
              className="mt-4 px-4 py-2 bg-red-500 text-white rounded shadow hover:bg-red-600"
              onClick={closeModal}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProfilePage;
