import React, { useEffect } from 'react'
import { useNotificationHook } from '../Hooks/connectionHook'

const Notification = () => {
  const {notifications , getNotifications} = useNotificationHook()
  useEffect(() => {
    getNotifications()
  },[])
  console.log(notifications)
  return (
    <div className="w-full max-w-sm bg-white border border-gray-200 rounded-lg shadow mt-4 ml-4">
        <div className="flex flex-col items-center p-4 ">
            <img className="w-24 h-24 mb-3 rounded-full shadow-lg" src="https://img.freepik.com/free-vector/smiling-redhaired-boy-illustration_1308-176664.jpg?semt=ais_hybrid" alt="Bonnie image"/>
            <h5 className="mb-1 text-xl font-medium ">Bonnie Green</h5>
            <div className="flex mt-4 md:mt-6">
                <button className="inline-flex items-center px-4 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800  focus:outline-none  dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800 mr-1">Accept</button>
                <button className="inline-flex items-center px-4 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800  focus:outline-none  dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Reject</button>
            </div>
        </div>
    </div>
  )
}

export default Notification
