import React, { useEffect, useRef, useState } from 'react';
import Header2 from '../Header/Header2';
import { useAddMessage, useFetchMsg } from '../Hooks/messageHooks';
import { useGetLoggedInUser } from '../Hooks/chatHooks';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';

const ChatPage = () => {
  const { chatId } = useParams();
  const { user } = useGetLoggedInUser();
  const { allMsg, setAllMsg } = useFetchMsg();
  const { addMessage } = useAddMessage();
  const [message, setMessage] = useState('');
  const socket = useRef(null);

  useEffect(() => {
    socket.current = io('http://localhost:8000');

    if (chatId) {
      socket.current.emit('join room', chatId);
    }

    socket.current.on('receivedMsg', (messageData) => {
      // Set senderId to check whether the message is from the current user or another user
      setAllMsg((prevAllMsg) => [
        ...prevAllMsg,
        {
          content: messageData.message,
          senderId: messageData.senderId, // Use senderId to determine sender
        },
      ]);
    });

    return () => {
      socket.current.disconnect();
    };
  }, [chatId]);

  const handleOnSubmit = async (event) => {
    event.preventDefault();
    if (user) {
      await addMessage(message, chatId);
      socket.current.emit('sendMessage', {
        message: message,
        chatId: chatId,
        senderId: user._id, // Send the senderId along with the message
      });
      setMessage('');
    } else {
      console.log('User not logged in');
    }
  };

  return (
    <div className="flex flex-col h-screen w-full bg-gray-100">
      <Header2 />
      <div className="flex flex-col flex-1 overflow-y-hidden">
        <div>{user?._id}</div>
        <div className="flex flex-col flex-1 overflow-y-auto p-4 space-y-4 bg-gray-50">
          {allMsg && allMsg.length > 0 ? (
            allMsg.map((msg, index) => (
              <div
                key={index}
                className={`flex ${
                  msg.senderId === user._id ? 'justify-end' : 'justify-start'
                }`}
              >
                <div
                  className={`p-3 rounded-lg max-w-xs break-words ${
                    msg.senderId === user._id
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-200 text-black'
                  }`}
                >
                  {msg.content}
                </div>
              </div>
            ))
          ) : (
            <h1 className="text-center text-gray-500">No messages available</h1>
          )}
        </div>
        <form
          onSubmit={handleOnSubmit}
          className="flex items-center p-4 bg-white border-t border-gray-300"
        >
          <input
            type="text"
            placeholder="Type a message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            className="flex-1 p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring focus:ring-blue-300"
          />
          <button
            type="submit"
            className="ml-4 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none"
          >
            Send
          </button>
        </form>
      </div>
    </div>
  );
};

export default ChatPage;
