import React, { useState } from 'react';
import './SearchPage.css';
import Header from '../Header/Header';
import { useGetAllChat, useGetAllUser } from '../Hooks/chatHooks';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';

const SearchPage = () => {
  const { alluser , error} = useGetAllUser()
  const {allChat , setAllChat} = useGetAllChat()


  const [filter ,setFilter] = useState("") ;

  const filteredData = allChat.filter(
    (item) =>
      item.chatName?.toLowerCase().includes(filter.toLowerCase()) 
  );

  if(error){
    return <div>error : {error} </div>
  }



  const clickHandler =()=>{
    setFilter("") ;
  }
  const url ="data:image/jpeg;base64,"

  return (
    <div className="search-page">
      <Header />
      <div className="search">
        <input type="text" value={filter} placeholder="Search..." onChange={(e)=>{setFilter(e.target.value)}} className='search-placeholder'/>
        <button className='search-button' onClick={clickHandler}><FontAwesomeIcon icon={faArrowLeft} /></button>

        {
          allChat ? (<div className="alluser" >  
            {
              filteredData.map(user => (
                <Link to={`/chat/${user._id}`}>
                <div className='alluser-1' >
                <img src={url} alt="" key={user._id} />
                <div className="alluser-name">{user.chatName}</div>
                </div >
                </Link>
              ))
            }
          </div>) : ( <div> no user found </div> )
        }
      </div>
    </div>
  );
};

export default SearchPage;
